$( ".draggable" ).draggable( {
    helper: "clone",
    appendTo: "body",
    //revert: false,
    //snap: ".ui-droppable",
    //stack: ".draggable",
    //scroll: false,
} );

function setDroppable(id) {
    $(id).droppable({
        accept: ".draggable",
        activeClass: "snaptarget-hover",
        drop: function (event, ui) {

            var ct = $(this);
            var item = $(ui.draggable);
            var origPos;
            var ctPos = ct.offset();
        
            if (item.is('.fr')) {
                origPos = item.offset();
                ct.append(item);
            } else {

                origPos = ui.offset;
                item = item.clone();
                ct.append(item);

                var dataId = ui.draggable[0].attributes.data_id.value
                stickersArray.push(dataId)

                item.removeClass("ui-draggable");
                item.addClass('fr');

                item.draggable({
                    containment: $(id),
                    obstacle: ".textDropable",
                    preventCollision: true
                }).resizable({ containment: $(id),});
            }

            switch(id){
                case '#top':
                    item.css({ left: origPos.left - ctPos.left - 1 });
                    break;
                case '#bottom':
                    item.css({ left: origPos.left - ctPos.left - 1 });
                    break;
                case '#right':
                    item.css({ top: origPos.top - ctPos.top - 1 });
                    break;    
                case '#left':
                    item.css({ top: origPos.top - ctPos.top - 1 });
                    break; 
            }

            // item.css({
            //     top: origPos.top - ctPos.top - 1,
            // });

            // item.css({
            //     top: origPos.top - ctPos.top - 1,
            //     left: origPos.left - ctPos.left - 1
            // });

            // if(window.matchMedia("(max-width: 767px)").matches){
            //     $('.fr > .sticker-parent').rotatable().resizable({
            //         disabled: true
            //     });
            // } else{
            //     item.rotatable()
                
            //     // .resizable({
            //     //     containment: $(id),
            //     //     //maxWidth: 80,
            //     //     //maxHeight: 80,
            //     //     snap: false,
            //     //     minWidth: 40,
            //     //     minHeight: 40,
            //     //     animateDuration: "fast",
            //     //     autoHide: true,
            //     // });
            // }

            if(fontColor == '') {
                $('.fr .sticker-parent svg path.c').css('fill', "#242424")
                $('.fr .sticker-parent svg circle.c').css('fill', "#242424")
            } else {
                $('.fr .sticker-parent svg path.c').css('fill', fontColor)
                $('.fr .sticker-parent svg circle.c').css('fill',fontColor)
            }

            $('<span class="click-me" data_id='+ dataId +'>X</span>').appendTo(item).click(removeElement);
        }
    }); 
}

function checkDefaultFont() {
    localStorage.setItem( 'font', 'rounded' );
}

function textValidation(id, counterClass, limit) {
    document.querySelector( id ).oninput = function ( e ) {
        let counter = e.target.value.length,
            counterElement = document.querySelector( counterClass );
        counter <= limit && (counterElement.innerHTML = counter)
    }
}

function setFontFamily(value, el) {
    switch(value) {
        case 'classic':
            el.classList.add( 'whitneysans' );
            break;
        case 'rounded':
            el.classList.add( 'chewy' );
            break;
        case 'script':
            el.classList.add( 'courgette' );
            break;
        case 'wide':
            el.classList.add( 'fyodor' );
            break;
        default:
            break;
    }
}

function createElementOnFrame( valueId, frameId, ls_key ) {
        counter++;
        let value = document.querySelector( valueId ).value;

        // Set values to localstorage
        localStorage.setItem(  ls_key , value)

        if ( value.length !== 0 ) {

           //if($(frameId + " .clear-text ").text() !== value) {

                if($(frameId).children('.textDropable').length) {
                    console.log(frameId)
                    $(frameId + ' > .textDropable').remove()
                }
                
                let font = localStorage.getItem( 'font' ),
                    div = document.createElement( 'div' ),
                    id = (frameId + counter).replace('#', ''),
                    pre = document.createElement('span');

                if(frameId == '#right' || frameId == '#left') {
                    div.className = 'textDropable rotate'
                }else {
                    div.className = 'textDropable'
                }

                if(fontColor == '') {
                    div.style.color = "#242424"
                } else {
                    div.style.color = fontColor
                }

                div.id = id;
                pre.className = 'clear-text'
                pre.innerHTML = value;
                div.appendChild(pre);

                setTimeout( () => {
                    textFit(document.querySelector('#'+id));
                    $('<span class="click-me">X</span>').appendTo('.textDropable').click(removeElement);
                }, 0);

                setFontFamily( font, div );
                document.querySelector( frameId ).append( div );

                switch(frameId) {
                    case '#top':
                        $( '#'+id ).draggable({ 
                            containment: frameId,
                            axis: 'x'
                        })  
                        break;
                    case '#bottom':
                        $( '#'+id ).draggable({ 
                            containment: frameId,
                        axis: 'x'
                        })  
                        break;    
                    case '#right':
                        $( '#'+id ).draggable({ 
                            containment: frameId,
                            axis: 'y'
                        })  
                        break;  
                    case '#left':
                        $( '#'+id ).draggable({ 
                            containment: frameId,
                            axis: 'y'
                        })  
                        break;  
                    default:
                        break;
                }  

                //$( '#'+id ).draggable({ containment: frameId })  
            //} 
        }
}

function checkExistedValue(id, length) {

    let existedValue = $('#' + id + ' > .textDropable > span.clear-text').text().length;
    console.log(existedValue)
    let finalValue = length-existedValue;

    $('#' + id + '-input').attr('maxlength', finalValue);
    $('.' + id + '-maxlength').html(finalValue)

}

function removeElement() {

    if($(this).parent().hasClass('textDropable')) {
        let parent = $(this).parent()
        let parentsParent = parent.parent()[0].id;
        $('#'+parentsParent+'-input').val('');
        $(this).parent().remove();
    } else {
        $(this).parent().remove();

        let removeItem = $(this)[0].attributes.data_id.value;
        stickersArray.splice( $.inArray(removeItem, stickersArray) , 1 );

        renderSelectedStickers();
    }

    setTimeout(()=>{
        checkExistedValue('top', 15);
        checkExistedValue('bottom', 15);
        checkExistedValue('right', 20);
        checkExistedValue('left', 20);
    }, 0)
}

function clearInputs() {

    document.querySelector( '#top-input' ).value = '',
    document.querySelector( '#right-input' ).value = '',
    document.querySelector( '#bottom-input' ).value = '',
    document.querySelector( '#left-input' ).value = '';
    
    let counters = document.querySelectorAll( '.limitor > span.co' );
    for ( let i = 0; i < counters.length; i++) {
        counters[i].innerHTML = 0;
    }
}

function clearInnerHtml(elementId) {
    document.querySelector(elementId).innerHTML = ''
}

